import React from "react";
import Burger from "../Burger/Burger";
import { Link } from "react-router-dom";
import CloseIcon from "../../assets/close.png";
import HomeIcon from "../../assets/home.png";
import { useSearchQuery, useSidebar, useFoodInfo } from "../../hooks";

/** @jsx jsx */
import { css, jsx } from "@emotion/core";
import {
  navStyles,
  labelStyles,
  inputStyles,
  spanStyles,
  closeStyles,
  linkStyles
} from "./styles";

const Nav = () => {
  const { searchQuery, resetSearchQuery, handleChange } = useSearchQuery();
  const { handleSidebar } = useSidebar();
  const { toggleFoodInfoOff } = useFoodInfo();

  const resetSearch = e => {
    toggleFoodInfoOff();
    resetSearchQuery();
  };

  return (
    <nav css={navStyles}>
      <Burger handleSidebar={handleSidebar} />
      <Link
        css={css`
          grid-area: home;
          justify-content: center;
          text-align: center;

          @media (min-width: 700px) {
            display: none;
          }
        `}
        to="/"
      >
        <img
          css={css`
            height: 30%;
            width: 30%;
          `}
          src={HomeIcon}
          alt="Home icon"
        ></img>
      </Link>
      <Link css={linkStyles} onClick={resetSearch} to="/">
        <p>dietFairy</p>
      </Link>

      <label css={labelStyles}>
        <input
          css={inputStyles}
          placeholder=" "
          value={searchQuery}
          onChange={handleChange}
          data-testid="search"
        />
        <span css={spanStyles}>Search for ingredients here...</span>
        {searchQuery.length > 0 && (
          <img
            css={closeStyles}
            src={CloseIcon}
            onClick={resetSearchQuery}
            alt="Close icon"
          />
        )}
      </label>
    </nav>
  );
};

export default Nav;
